package kanoodle.algorithm;

import java.util.Map;

import com.beust.jcommander.JCommander;

import kanoodle.algorithm.game.Level;
import kanoodle.algorithm.game.Piece;
import kanoodle.algorithm.game.solvers.Solver;
import kanoodle.algorithm.game.util.ParserUtil;

public class App {
    public static void main(String[] argv) throws Exception {
        Args args = new Args();
        JCommander.newBuilder().addObject(args).build().parse(argv);
        args.validate();

        Map<Character, Piece> pieces = ParserUtil.parsePieces();
        Level level = new Level(args.getConfig());
        Solver solver = args.getAlgorithm(pieces);
        System.out.println(solver.solve(level).toString(pieces, args.isNormalMode()));
    }
}
